import React from 'react';
import Cube from '../components/Cube'
import { Swappable } from '@shopify/draggable';

export default function Home () {
  const [homeError, setHomeError] = React.useState(true);
  React.useEffect(() => {
    const swappable = new Swappable(document.querySelectorAll('div'), {
      draggable: '.cube-three-side'
    });
  
    swappable.on('swappable:start', () => console.log('swappable:start'));
    swappable.on('swappable:swapped', () => console.log('swappable:swapped'));
    swappable.on('swappable:stop', () => console.log('swappable:stop')); 
    return () => {
      swappable.destroy();
    }
  }, []);
  return (
    <div className='home'>
      <div className='home-greet' style={{fontSize: '160px'}}>
        Hi,
      </div>
      <div className='home-greet'>
        my name is
      </div>
      <div className='home-cubes' onClick={() => {
        setHomeError(false);
      }}>
        <Cube front='K' side='A' top='R'/>
        <Cube front='H' side='I' top='Y'/>
        <Cube front='N' side='G' top='A'/>
        <Cube front='E' side='E' top=' '/>
        <Cube front=' ' side='L' top='N'/>
      </div>
      {homeError && (
        <div className='home-greet' style={{fontSize: '50px'}}>
          Oh no, my name is spelt wrong! Can you swap the cubes around?
        </div>
      )}
    </div>
  )
}
